public void ConsumeDocument(Document doc)
{
   if (doc.IsInt())
   {
      int intValue = doc.AsInt();
      // do work with intValue
   }
   else if (doc.IsString())
   {
      string stringValue = doc.AsString();
      // do work with stringValue
   }
}

